import React, {useState} from "react";
import "./Gameboard.css"; 
import {getRandomWord} from "../../utilities/api"
import {createBoardState} from "../../utilities/helpers"
import GuessRow from "../GuessRow/GuessRow"

export default function Gameboard(){

    // WordleFlex states 

    const startingGuesses = 6
    const startingLetters = 5

    const [keywordLetters, setKeywordLetters] = useState(startingLetters) 
    // minimum 3, maximum 8 - letters in the keyword
    const [keyword, setKeyword] = useState(getRandomWord(5)) 
    // this is the word the player is guessing
    const [guessNumber, setGuessNumber] = useState(1) 
    // this tracks which guess we are on in the game. used to update the guess rows once a guess is evaluated, and check whether the game ends after 5 guesses 
    const [maxGuesses, setMaxGuesses] = useState(startingGuesses) 
    // players cannot change this, but set to a state variable for future scalability and flexibility
    const [guess, setGuess] = useState("")    
    // the guess that the player enters, gets updated by the guess form
    const [boardState, setBoardState] = useState(createBoardState())
    // running tally of the guesses across the whole round

    const updateSettings = (event) => {
        event.preventDefault()
        console.log("test")
        setKeywordLetters(4)
            //document.getElementById("LettersInput").value)
        // setMaxGuesses(document.getElementById("GuessesInput").value)
    }

    return (
        <>
        <div className="container">    
           {[...Array(maxGuesses).keys()].map((arg) => {
            return(
                <GuessRow
                    key = {arg}
                    id = {arg}
                    keywordLetters = {keywordLetters} 
                />
            )
           })}
        </div>
        <form className="settings" onSubmit={updateSettings}>
            <label>Letters:</label>
            <input 
                id="LettersInput" 
                type="number" 
                min={3} 
                max={8}
                defaultValue={startingLetters}
            />
            <label>Guesses:</label>
            <input 
                id="GuessesInput" 
                type="number" 
                min={3} 
                max={8}
                defaultValue={startingGuesses}
            />
            <input 
                type="submit" 
                value="Update"
            />
        </form>
        <form className="guess">
            <label>Guess:</label>
            <input type="text" minLength={keywordLetters} maxLength={keywordLetters} />
            <input 
                type="submit" 
                value="Guess!"
            />
        </form>
        </>
    )
}
