import React, {useState, useEffect} from "react";
import "./Gameboard.css"; 
import {getRandomWord} from "../../utilities/api"
import {createBoardState} from "../../utilities/helpers"
import GuessRow from "../GuessRow/GuessRow"

export default function Gameboard(){

    // WordleFlex states 

    const startingGuesses = 6
    const startingLetters = 5

    const [keywordLetters, setKeywordLetters] = useState(startingLetters) 
    // minimum 3, maximum 8 - letters in the keyword
    const [keyword, setKeyword] = useState(getRandomWord(keywordLetters)) 
    // this is the word the player is guessing
    const [guessNumber, setGuessNumber] = useState(0) 
    // this tracks which guess we are on in the game. used to update the guess rows once a guess is evaluated, and check whether the game ends after 5 guesses 
    const [maxGuesses, setMaxGuesses] = useState(startingGuesses) 
    // players cannot change this, but set to a state variable for future scalability and flexibility
    const [guess, setGuess] = useState("")    
    // the guess that the player enters, gets updated by the guess form
    const [boardState, setBoardState] = useState(createBoardState(keywordLetters,maxGuesses))
    // running tally of the guesses across the whole round


    // update the KeyWord Letters

    const updateKeywordLetters = (event) => {
        event.preventDefault()
        setKeywordLetters(Number(event.target.value))
        console.log(Number(event.target.value))
        console.log(keywordLetters)
    }
    useEffect(() => {
        setKeyword(getRandomWord(keywordLetters))
    }, [keywordLetters])

    const updateMaxGuesses = (event) => {
        event.preventDefault()
        setMaxGuesses(Number(event.target.value))
    }

    const updateGuess = (event) => {
        event.preventDefault()
        setGuess(event.target.value)
    }

    const submitGuess = (event) => {
        event.preventDefault()
        console.log("guess")

    }

    return (
        <>
        <div className="container">    
           {[...Array(maxGuesses).keys()].map((arg) => {
            return(
                <GuessRow
                    key = {arg}
                    id = {arg}
                    keywordLetters = {keywordLetters} 
                />
            )
           })}
        </div>
        <form className="settings">
            <label>Letters:</label>
            <input 
                id="LettersInput" 
                type="number" 
                min={4} 
                max={8}
                value={keywordLetters}
                onChange={updateKeywordLetters}
            />
            <label>Guesses:</label>
            <input 
                id="GuessesInput" 
                type="number" 
                min={2} 
                max={8}
                value={maxGuesses}
                onChange={updateMaxGuesses}
            />
        </form>
        <form className="guess" onSubmit={submitGuess}>
            <label>Guess:</label>
            <input 
                type="text" 
                minLength={keywordLetters} 
                maxLength={keywordLetters}
                onChange={updateGuess}
            />
            <input 
                type="submit" 
                value="Guess"
            />
        </form>
        </>
    )
}
